            ctx.lineCap = "round";
            ctx.lineJoin = "round";
            if (zoom < 1) {
                ctx.lineWidth = ctx.lineWidth / zoom;
            }
            ctx.setTransform(zoom, 0, 0, zoom, 0, 0);
            ctx.clearRect(0, 0, width, height);
