    // eslint-disable-next-line
    calculateInkSize() {
        let minimumX = -1;
        let minimumY = -1;
        let maximumX = -1;
        let maximumY = -1;
        // eslint-disable-next-line
        let collectionData = processPathData(this.outputString);
        const zoomvalue = this.pdfViewerBase.getZoomFactor();
        // eslint-disable-next-line
        for (let k = 0; k < collectionData.length; k++) {
            // eslint-disable-next-line
            let val = collectionData[k];
            if (minimumX === -1) {
                // eslint-disable-next-line
                minimumX = (val['x']);
                // eslint-disable-next-line
                maximumX = (val['x']);
                // eslint-disable-next-line
                minimumY = (val['y']);
                // eslint-disable-next-line
                maximumY = (val['y']);
            }
            else {
                // eslint-disable-next-line
                let point1 = (val['x']);
                // eslint-disable-next-line
                let point2 = (val['y']);
                if (minimumX >= point1) {
                    minimumX = point1;
                }
                if (minimumY >= point2) {
                    minimumY = point2;
                }
                if (maximumX <= point1) {
                    maximumX = point1;
                }
                if (maximumY <= point2) {
                    maximumY = point2;
                }
            }
        }
        const newdifferenceX = maximumX - minimumX;
        const newdifferenceY = maximumY - minimumY;
        // eslint-disable-next-line max-len
        return { x: (minimumX / zoomvalue), y: (minimumY / zoomvalue), width: (newdifferenceX / zoomvalue), height: (newdifferenceY / zoomvalue) };
    }
