    /**
     * @param bounds
     * @param position
     * @private
     */
    // eslint-disable-next-line
    addSignatureCollection(bounds, position) {
        let minimumX = -1;
        let minimumY = -1;
        let maximumX = -1;
        let maximumY = -1;
        // eslint-disable-next-line
        let collectionData = processPathData(this.outputString);
        // eslint-disable-next-line
        let newCanvas = document.createElement('canvas');
        // eslint-disable-next-line
        let context = newCanvas.getContext('2d');
        // eslint-disable-next-line
        let imageString;
        const signatureType = this.pdfViewerBase.currentSignatureAnnot.shapeAnnotationType;
        if (signatureType === 'HandWrittenSignature') {
            if (collectionData.length !== 0) {
                // eslint-disable-next-line
                for (let k = 0; k < collectionData.length; k++) {
                    // eslint-disable-next-line
                    let val = collectionData[k];
                    if (minimumX === -1) {
                        // eslint-disable-next-line
                        minimumX = (val['x']);
                        // eslint-disable-next-line
                        maximumX = (val['x']);
                        // eslint-disable-next-line
                        minimumY = (val['y']);
                        // eslint-disable-next-line
                        maximumY = (val['y']);
                    }
                    else {
                        // eslint-disable-next-line
                        let point1 = (val['x']);
                        // eslint-disable-next-line
                        let point2 = (val['y']);
                        if (minimumX >= point1) {
                            minimumX = point1;
                        }
                        if (minimumY >= point2) {
                            minimumY = point2;
                        }
                        if (maximumX <= point1) {
                            maximumX = point1;
                        }
                        if (maximumY <= point2) {
                            maximumY = point2;
                        }
                    }
                }
                const newdifferenceX = maximumX - minimumX;
                const newdifferenceY = maximumY - minimumY;
                let differenceX = newdifferenceX / 100;
                let differenceY = newdifferenceY / 100;
                let left = 0;
                let top = 0;
                if (bounds) {
                    newCanvas.width = position.currentWidth;
                    newCanvas.height = position.currentHeight;
                    differenceX = newdifferenceX / (bounds.width);
                    differenceY = newdifferenceY / (bounds.height);
                    left = bounds.x - position.currentLeft;
                    top = bounds.y - position.currentTop;
                }
                else {
                    newCanvas.width = 100;
                    newCanvas.height = 100;
                }
                context.beginPath();
                for (let n = 0; n < collectionData.length; n++) {
                    // eslint-disable-next-line
                    let val = collectionData[n];
                    // eslint-disable-next-line
                    let point1 = ((val['x'] - minimumX) / differenceX) + left;
                    // eslint-disable-next-line
                    let point2 = ((val['y'] - minimumY) / differenceY) + top;
                    // eslint-disable-next-line
                    if (val['command'] === 'M') {
                        context.moveTo(point1, point2);
                        // eslint-disable-next-line
                    }
                    else if (val['command'] === 'L') {
                        context.lineTo(point1, point2);
                    }
                }
                context.stroke();
                context.closePath();
                imageString = newCanvas.toDataURL();
            }
        }
        else if (signatureType === 'SignatureText') {
            imageString = this.outputString;
        }
        else {
            imageString = this.outputString;
        }
        if (bounds) {
            this.saveImageString = imageString;
        }
        else {
            // eslint-disable-next-line
            let signCollection = {};
            signCollection['sign_' + this.pdfViewerBase.imageCount] = this.outputString;
            this.outputcollection.push(signCollection);
            // eslint-disable-next-line
            let signature = [];
            signature.push({ id: 'sign_' + this.pdfViewerBase.imageCount, imageData: imageString, signatureType: signatureType, fontFamily: this.pdfViewerBase.currentSignatureAnnot.fontFamily });
            this.signaturecollection.push({ image: signature, isInitial: this.pdfViewerBase.isInitialField });
            this.pdfViewerBase.imageCount++;
        }
    }
