    setAttributeHtml(element, attributes) {
        const keys = Object.keys(attributes);
        for (let i = 0; i < keys.length; i++) {
            if (keys[i] !== 'style') {
                element.setAttribute(keys[i], attributes[keys[i]]);
            }
            else {
                this.applyStyleAgainstCsp(element, attributes[keys[i]]);
            }
        }
    }
    applyStyleAgainstCsp(svg, attributes) {
        const keys = attributes.split(';');
        for (let i = 0; i < keys.length; i++) {
            const attribute = keys[i].split(':');
            if (attribute.length === 2) {
                svg.style[attribute[0].trim()] = attribute[1].trim();
            }
        }
    }
